if (msg.magic != 0xadbccbda) {
    return null;
}

msg.version = data.readUInt32BE(4);
msg.type = data.readUInt32BE(8);
switch (msg.type) {
    case 0: msg.topic = 'heartbeat'; break;
    case 1: msg.topic = 'status'; break;
    case 2: msg.topic = 'decode'; break;
    case 3: msg.topic = 'clear'; break;
    case 4: msg.topic = 'reply'; break;
    case 5: msg.topic = 'qso-logged'; break;
