if (msg.topic.length > 3) {
    const topic = msg.topic.split('/');
    const stream_type = topic[1];   // waterfall, panadapter
    const stream_id = topic[2];

    const streams = flow.get(stream_type) || {};
    if (topic.length === 4 && topic[3] === 'removed') {
        delete streams[stream_id];
    } else {
        const stream = {...streams[stream_id], ...msg.payload};
        streams[stream_id] = stream;
    }
    node.status({ fill: 'green', shape: 'circle', text: stream_type});
    flow.set(stream_type, streams);    
}

return msg;

